import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Location } from '@angular/common';
import { Login } from '../login';
import { LoginService } from '../login.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  users: Login[]= [];
  static currentUser: Login | undefined;
  isLoggedIn: boolean= false;

  constructor(private loginService: LoginService,
    private location: Location,
    private router: Router){}

  ngOnInit(): void {
    this.getUsers();
    if(LoginComponent.currentUser){
      console.log(`user logged in!`)
      console.log(LoginComponent.currentUser);
    }
    else{
      console.log(`Houston we have a problem`)
    }
    if(LoginComponent.currentUser){
      this.isLoggedIn= true;
    }
  }

  setUser(user : Login): void{
    //this.currentUser= user;
    LoginComponent.currentUser= user;
  }

  getCurrentUser(): any{
    //return this.currentUser;
    return LoginComponent.currentUser;
  }

  getUsers(): void {
    this.loginService.getUsers()
    .subscribe(users => this.users = users);
  }

  add(username: string): void {
    username = username.trim();
    if (!username) { return; }
    this.loginService.addUser({ username } as Login)
      .subscribe(user => {
        this.users.push(user);
      });
  }

  goBack(): void{
    this.location.back();
  }

  login(username: string): void{
    //Function to search if the user of this username exists or not
   for(let i= 0;i < this.users.length; i+=1){
    if(username === this.users[i].username){
      //this.currentUser= this.users[i];
      LoginComponent.currentUser= this.users[i];
      break;
    }
   }
   //console.log(this.currentUser);
   if(LoginComponent.currentUser){
    console.log(this.users);
    //this.currentUser.isLoggedIn= true;
    LoginComponent.currentUser.isLoggedIn= true;
    console.log(LoginComponent.currentUser);
    //this.loginService.updateUser(this.currentUser);
    this.loginService.updateUser(LoginComponent.currentUser);
    this.isLoggedIn= true;
    this.router.navigateByUrl("/products");
    //console.log(this.users);
    //console.log(this.currentUser);
    //this.goBack();
   }
  }
  logout(): void{
    /** Function to log user out of store */
    if(LoginComponent.currentUser){
      //this.currentUser.isLoggedIn= false;
      LoginComponent.currentUser.isLoggedIn= false;
      //console.log(this.currentUser);
      for(let i=0;i<this.users.length;i+=1){
        if(LoginComponent.currentUser.username === this.users[i].username){
          //this.users[i]= this.currentUser;
          this.users[i]= LoginComponent.currentUser;
          break;
        }
      }
      //this.loginService.updateUser(this.currentUser);
      this.loginService.updateUser(LoginComponent.currentUser);
      //this.currentUser= undefined;
      LoginComponent.currentUser= undefined;
      //console.log(this.currentUser);
      //console.log(this.users);
      this.isLoggedIn= false;
      //this.goBack();
    }
  }
}
