import { Component, OnInit } from '@angular/core';
import { Observable, Subject } from 'rxjs';
import {
  debounceTime, distinctUntilChanged, switchMap
} from 'rxjs/operators';
import { Location } from '@angular/common';
import { Login } from '../login';
import { LoginService } from '../login.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  users: Login[]= [];
  username$!: Observable<Login[]>;
  currentUser!: Login; 
  temp!: Login;

  constructor(private loginService: LoginService,
    private location: Location){}

  ngOnInit(): void {
    this.getUsers();
  }

  getUsers(): void {
    this.loginService.getUsers()
    .subscribe(users => this.users = users);
  }

  //we need a function that gets the name of what is entered into the box. This will be sent to the backend to check
  //if the name is an admin or a customer
  /**
  getUsername(username: string): void{
    //this.productService.getProducts()
    //.subscribe(cart => this.cart= cart);
    this.username= username;
  }
  */

  setUser(user: Login): void{
    this.currentUser.username= user.username;
    this.currentUser.id= user.id;
    this.currentUser.cart= user.cart;
  }

  add(username: string): void {
    username = username.trim();
    if (!username) { return; }
    this.loginService.addUser({ username } as Login)
      .subscribe(user => {
        this.users.push(user);
      });
  }

  goBack(): void{
    this.location.back();
  }

  update(): void{
    if(this.currentUser){
      this.loginService.updateUserStatus(this.currentUser)
      .subscribe(
        () => this.goBack()
        );
    }
  }

  search(username: string): void{
    //Function to search if the user of this username exists or not
    this.loginService.findUser(username)
    .subscribe(
      //user => this.setUser(user)
      x => this.temp= x
      );
    console.log(this.temp);
    if(this.temp){
      this.setUser(this.temp);
      this.loginService.testMessage(this.currentUser);
      this.currentUser.isLoggedIn= true;
      this.update();
    }
  }

  changeLoginStatus(): void{
    if(this.currentUser.isLoggedIn == true){
      this.currentUser.isLoggedIn= false;
    }
    else{
      this.currentUser.isLoggedIn= true;
    }
    this.update();
  }

  logout(): void{
    /** Function to log user out of store */
    this.changeLoginStatus();
  }
}
