import { Component } from '@angular/core';
import { Login } from './login';
import { LoginService } from './login.service';
import { LoginComponent } from './login/login.component';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'e-store';
  static currentUser: Login | undefined= undefined;
  currUser: Login | undefined= undefined;
  isAdmin: boolean= false;
  constructor(private userService: LoginService,
    private userComponent: LoginComponent){}

  setUser(user: Login | undefined): void{
    if(user){
      AppComponent.currentUser= user;
      this.currUser= AppComponent.currentUser;
      if(AppComponent.currentUser.username === "admin"){
        this.isAdmin= true;
      }
      else{
        this.isAdmin= false;
      }
    }
    else{
      AppComponent.currentUser= undefined;
      this.currUser= undefined
      this.isAdmin= false;
    }
  }
}
